		~Base(){cout<<"Destruct Base"<<endl;} 
};
class Der: public Base{ 
	public: 
		Der(){cout<<"Der"<<endl;} 
		Der(int i): Base(i) {cout<<"Der"<<i<<endl;} 
		~Der(){cout<<"Destruct Der"<<endl;} 
}; 
