For the  code shown answer the following questions	:
\begin{lstlisting}[language=cpp]
class A {
public:
	int f() {return 1;	}
	virtual int g() {return 2;}
};
class B: public A {
public:
	int f() {return 3;}
	virtual int g() {return 4;}
};
class C: public A {
public:
	virtual int g() {return 5;}
};
int main() {
	A *pa = new A();
	A *pb = new B();
	A *pc = new C();

	cout << pa->f() << endl; /* line 1*/
	cout << pa->g() << endl; /* line 2*/
	cout << pb->f() + pb->g() << endl; /* line 3*/
	cout << pc->f() << endl; /* line 4*/
	cout << pc->g() << endl; /* line 5*/
	return 0;
}
\end{lstlisting}

 What is the output of this program?
\begin{lstlisting}[language=cpp]

#include <iostream>
using namespace std;
class sample     {
	public:
    int x, y;
    sample() {};
    sample(int, int);
    sample operator + (sample);
};
sample::sample (int a, int b) {
	x = a;
    y = b;
}
sample sample::operator+ (sample param) {
    sample temp;
    temp.x = x + param.x;
    temp.y = y + param.y;
    return (temp);
} 
int main () {
    sample a (4,1);
    sample b (3,2);
    sample c;
    c = a + b;
    cout << c.x << "," << c.y;
    return 0;
}
\end{lstlisting}


